import React, { useState } from "react";
import axios from "axios";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    dob: "",
    password: "",
    gender: "Male",
    about: "",
  });
  
  const [genderOptions, setGenderOptions] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:5000/api/users/genders")
      .then((response) => {
        setGenderOptions(response.data);
      })
      .catch((error) => console.error("Error fetching gender options:", error));
  }, []);
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/users/register", formData);
      alert("User Registered Successfully!");
    } catch (error) {
      alert("Error: " + error.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white border border-black p-6 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-4">Register User</h2>
        <form onSubmit={handleSubmit} className="flex flex-col gap-3">
          <input
            type="text"
            name="name"
            placeholder="Name"
            onChange={handleChange}
            required
            className="border border-gray-300 p-2 rounded"
          />
          <input
            type="number"
            name="age"
            placeholder="Age"
            onChange={handleChange}
            required
            className="border border-gray-300 p-2 rounded"
          />
          <input
            type="date"
            name="dob"
            onChange={handleChange}
            required
            className="border border-gray-300 p-2 rounded"
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
            required
            className="border border-gray-300 p-2 rounded"
          />
          <select
            name="gender"
            onChange={handleChange}
            className="border border-gray-300 p-2 rounded"
          >
            <option>Male</option>
            <option>Female</option>
            <option>Other</option>
          </select>
          <textarea
            name="about"
            placeholder="About"
            onChange={handleChange}
            maxLength="5000"
            className="border border-gray-300 p-2 rounded"
          ></textarea>
          <button
            type="submit"
            className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
